package com.day4Operators;

public class ConsitionalOperatorsdemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double number = -5.5;
		String result;
		result = (number > 0.0) ? "positive" : "not positive";
		System.out.println(number + " is " + result); // the result would be not positive 


	}

}
