package com.day4Operators;

public class Incremental_DecrementalDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x= 20;
		
		System.out.println (x++); // x the result of x will be 20. It first display the original value of x which is 20 and it takes into account x as 21 for the following anaysis
		System.out.println (++x); //22
		System.out.println (x--); //21
		System.out.println (--x); //20
	}

}
