package com.day4Operators;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean a = true; // booleab data types
		boolean b = false;
		System.out.println("a && b = " + (a && b)); // output would be false
		System.out.println("a || b = " + (a || b)); // output would be true
		System.out.println("!(a && b) = " + !(a && b)); // true 1 !is negating this equation 

	}

}
