package com.day4Operators;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a, b ; // this is called declaration you can also initialize the variables here. like this  int a=40, b=40;
		a=40; // this is called initializing of the variables 
		b=40; // //  initializing of the variables 
		
		System.out.println ("a==b = " + (a==b));// result would be true
		System.out.println ("a!=b = " + (a!=b));// false because a is equal to b. != means not equal
		System.out.println ("a>b = " + (a>b)); // false
		System.out.println ("a<b = " + (a<b)); // false
		System.out.println ("a>=b = " + (a>=b)); // true
		System.out.println ("a<=b = " + (a<=b)); // true
	}

}
