package com.day4loops;

public class OldNumbersDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// write a program that will print old numbers from 1 to 10
		
		for(int i=1; i<=10; i++) {
			if(i%2!=0)
				System.out.println(i + " " + i);
		}
			
		
	}

}


