package com.day4loops;

public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//write a program that will print when 1 to 10 using while loop
		int i = 1;
		while (i <= 10) {
			System.out.println(i);
			i++; // // this business logic will print 1 to 10 incrementally
		}
	}
}

